#!/usr/bin/python3

import base64
import binascii
from morse_code_decoder import decryption

initial_string = ""

# First, decode using base64:
base64_decoded = base64.b64decode(initial_string).decode()
# base64_decoded is a Morse Code encoded string.

# Decode each line of base64_decoded (split("\n")) into Morse Code:
morse_code_decoded = ""
for line in base64_decoded.split("\n"):
	morse_code_decoded += decryption(line) + " "
morse_code_decoded = morse_code_decoded[:-1]
# morse_code_decoded is a binary encoded string.

# Decode each line of morse_code_decoded (split(" ")) into ascii letters:
ascii_decoded = ""
for binary in morse_code_decoded.split(" "):
	binary = "0b" + binary
	n = int(binary, 2)
	ascii_decoded += str(n.to_bytes((n.bit_length() + 7) // 8, 'big').decode())
# ascii_decoded is a ROT47 encoded string.

# Using ROT47 Online decoder so print what I have so far...
print(ascii_decoded)
